import { useState } from "react";
import { useNavigate } from "react-router-dom";

function TaskForm({ addTask }) {
  const [form, setForm] = useState({ title: "", description: "", completed: false });
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm({ ...form, [name]: type === "checkbox" ? checked : value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    addTask(form);
    navigate("/");
  };

  return (
    <form onSubmit={handleSubmit} className="task-form">
      <input type="text" name="title" placeholder="Título" value={form.title} onChange={handleChange} required />
      <textarea name="description" placeholder="Descripción" value={form.description} onChange={handleChange} required />
      <label>
        <input type="checkbox" name="completed" checked={form.completed} onChange={handleChange} />
        ¿Completada?
      </label>
      <button type="submit">Guardar</button>
    </form>
  );
}

export default TaskForm;
