import { Routes, Route, Link } from "react-router-dom";
import Home from "/pages/Home";
import TaskDetail from "pages/TaskDetail";
import CreateTask from "pages/CreateTask";
import { useState } from "react";
import tasksData from "data/tasks.json";
import "./styles/app.css";

function App() {
  const [tasks, setTasks] = useState(tasksData);

  const addTask = (task) => {
    setTasks([...tasks, { ...task, id: tasks.length + 1, date: new Date().toLocaleDateString() }]);
  };

  return (
    <div className="app-container">
      <nav className="navbar">
        <Link to="/">Inicio</Link>
        <Link to="/create">Nueva Tarea</Link>
      </nav>

      <Routes>
        <Route path="/" element={<Home tasks={tasks} />} />
        <Route path="/task/:id" element={<TaskDetail tasks={tasks} />} />
        <Route path="/create" element={<CreateTask addTask={addTask} />} />
      </Routes>
    </div>
  );
}

export default App;
