import TaskItem from "../components/TaskItem";
import { Link } from "react-router-dom";

function Home({ tasks }) {
  return (
    <div>
      <h1>Lista de Tareas</h1>
      {tasks.map((task) => (
        <Link key={task.id} to={`/task/${task.id}`}>
          <TaskItem task={task} />
        </Link>
      ))}
    </div>
  );
}

export default Home;
