import { useEffect, useState } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";

function App() {
  // Lista de usuarios
  const [usuarios, setUsuarios] = useState([]);

  // Estado para el formulario
  const [form, setForm] = useState({
    nombre: "",
    apellido: "",
    direccion: "",
    telefono: "",
    celular: "",
    fecha_nacimiento: "",
    email: ""
  });

  // Estado para edición y filtro
  const [editId, setEditId] = useState(null);
  const [filtro, setFiltro] = useState("");

  // Cargar usuarios desde el backend
  const cargarUsuarios = () => {
    axios
      .get("http://localhost:3002/usuarios")
      .then((res) => setUsuarios(res.data))
      .catch((err) => console.error("Error al cargar usuarios:", err));
  };

  useEffect(() => {
    cargarUsuarios();
  }, []);

  // Manejar cambios en el formulario
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // Guardar nuevo usuario o actualizar existente
  const handleSubmit = (e) => {
    e.preventDefault();

    if (editId) {
      // Editar usuario
      axios
        .put(`http://localhost:3002/usuarios/${editId}`, form)
        .then(() => {
          setEditId(null);
          resetForm();
          cargarUsuarios();
        })
        .catch((err) => console.error("Error al actualizar:", err));
    } else {
      // Crear usuario
      axios
        .post("http://localhost:3002/usuarios", form)
        .then(() => {
          resetForm();
          cargarUsuarios();
        })
        .catch((err) => console.error("Error al agregar:", err));
    }
  };

  // Eliminar usuario
  const handleDelete = (id) => {
    axios
      .delete(`http://localhost:3002/usuarios/${id}`)
      .then(() => cargarUsuarios())
      .catch((err) => console.error("Error al eliminar:", err));
  };

  // Cargar usuario en el form para editar
  const handleEdit = (usuario) => {
    setEditId(usuario.id);
    setForm({
      nombre: usuario.nombre,
      apellido: usuario.apellido,
      direccion: usuario.direccion,
      telefono: usuario.telefono,
      celular: usuario.celular,
      fecha_nacimiento: usuario.fecha_nacimiento,
      email: usuario.email
    });
  };

  // Resetear formulario
  const resetForm = () => {
    setForm({
      nombre: "",
      apellido: "",
      direccion: "",
      telefono: "",
      celular: "",
      fecha_nacimiento: "",
      email: ""
    });
  };

  // Filtrar usuarios por nombre o email
  const usuariosFiltrados = usuarios.filter(
    (u) =>
      u.nombre.toLowerCase().includes(filtro.toLowerCase()) ||
      u.email.toLowerCase().includes(filtro.toLowerCase())
  );

  return (
    <div className="container mt-4">
      <h1 className="mb-4">Usuarios</h1>

      {/* Formulario */}
      <form onSubmit={handleSubmit} className="mb-4">
        <div className="row">
          <div className="col">
            <input
              name="nombre"
              value={form.nombre}
              onChange={handleChange}
              placeholder="Nombre"
              className="form-control"
              required
            />
          </div>
          <div className="col">
            <input
              name="apellido"
              value={form.apellido}
              onChange={handleChange}
              placeholder="Apellido"
              className="form-control"
              required
            />
          </div>
          <div className="col">
            <input
              name="direccion"
              value={form.direccion}
              onChange={handleChange}
              placeholder="Dirección"
              className="form-control"
            />
          </div>
        </div>

        <div className="row mt-2">
          <div className="col">
            <input
              name="telefono"
              value={form.telefono}
              onChange={handleChange}
              placeholder="Teléfono"
              className="form-control"
            />
          </div>
          <div className="col">
            <input
              name="celular"
              value={form.celular}
              onChange={handleChange}
              placeholder="Celular"
              className="form-control"
            />
          </div>
          <div className="col">
            <input
              type="date"
              name="fecha_nacimiento"
              value={form.fecha_nacimiento}
              onChange={handleChange}
              className="form-control"
            />
          </div>
        </div>

        <div className="row mt-2">
          <div className="col">
            <input
              type="email"
              name="email"
              value={form.email}
              onChange={handleChange}
              placeholder="Email"
              className="form-control"
              required
            />
          </div>
          <div className="col">
            <button type="submit" className="btn btn-primary">
              {editId ? "Actualizar" : "Agregar"}
            </button>
          </div>
        </div>
      </form>

      {/* Filtro */}
      <input
        type="text"
        placeholder="Filtrar por nombre o email"
        className="form-control mb-3"
        value={filtro}
        onChange={(e) => setFiltro(e.target.value)}
      />

      {/* Tabla */}
      <table className="table table-striped">
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Apellido</th>
            <th>Dirección</th>
            <th>Teléfono</th>
            <th>Celular</th>
            <th>Fecha de Nacimiento</th>
            <th>Email</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {usuariosFiltrados.map((u) => (
            <tr key={u.id}>
              <td>{u.nombre}</td>
              <td>{u.apellido}</td>
              <td>{u.direccion}</td>
              <td>{u.telefono}</td>
              <td>{u.celular}</td>
              <td>{new Date(u.fecha_nacimiento).toLocaleDateString()}</td>
              <td>{u.email}</td>
              <td>
                <button
                  className="btn btn-warning btn-sm me-2"
                  onClick={() => handleEdit(u)}
                >
                  Editar
                </button>
                <button
                  className="btn btn-danger btn-sm"
                  onClick={() => handleDelete(u.id)}
                >
                  Borrar
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
